import React, { useState, useEffect } from "react";
import classes from "./AddRecipe.module.scss";
import icons from "../../img/icons.svg";
const AddRecipe = (props) => {
  const onHideHandler = () => {
    props.setAddingRecipe(false);
  };
  const categoryArr = [
    "vegetarian",
    "non-vegetarian",
    "breakfast",
    "lunch",
    "dinner",
    "desserts",
    "pescatarian",
  ];
  const [ingredients, setIngredients] = useState([{ value: null }]);
  const [directions, setDirections] = useState([{ value: null }]);
  const [enteredName, setEnteredName] = useState("");
  const [enteredCuisine, setEnteredCuisine] = useState();
  const addIngredientHandler = () => {
    const val = [...ingredients];
    val.push({ value: null });
    setIngredients(val);
  };
  const onIngredientChangeHandler = (i, e) => {
    const val = [...ingredients];
    val[i].value = e.target.value;
    setIngredients(val);
  };
  const addDirectionHandler = () => {
    const val = [...directions];
    val.push({ value: null });
    setDirections(val);
  };
  const onDirectionChangeHandler = (i, e) => {
    const val = [...directions];
    val[i].value = e.target.value;
    setDirections(val);
  };
  console.log(ingredients);

  const onNameChangeHandler = (e) => {
    setEnteredName(e.target.value);
    // console.log(.target.value);
  };
  const onCuisineChangeHandler = (e) => {
    // setEnteredName(e.target.value);
    // console.log(.target.value);
  };
  const onCategorySelectHandler = (e) => {
    // setEnteredName(e.target.value);
    // console.log(.target.value);
  };
  const onServingsChangeHandler = (e) => {
    // setEnteredName(e.target.value);
    // console.log(.target.value);
  };
  const onCookingTimeChangeHandler = (e) => {
    // setEnteredName(e.target.value);
    // console.log(.target.value);
  };
  return (
    <div className={classes.overlay}>
      <div className={classes.container}>
        <div>
          <button className={classes.exit} onClick={onHideHandler}>
            <svg>
              <use xlinkHref={`${icons}#icon-exit`}></use>
            </svg>
          </button>
        </div>

        <div className={classes.details_one}>
          <h4>Add Recipe</h4>
          <form className={classes.form}>
            <div className={classes.name}>
              <label> Name: </label>
              <input type="text" onChange={onNameChangeHandler} />
            </div>
            <div className={classes.cuisine}>
              <label> Cuisine: </label>
              <input type="text" onChange={onCuisineChangeHandler} />
            </div>
            <div className={classes.category}>
              <label> Category: </label>
              <span>
                {categoryArr.map((category) => {
                  return (
                    <button
                      className={classes.categoryButton}
                      onClick={onCategorySelectHandler}
                    >
                      {category}
                    </button>
                  );
                })}
              </span>
            </div>
            <div className={classes.extrainfo}>
              <span>
                <label> Servings: </label>
                <input type="text" />
              </span>
              <span>
                <label> Cooking Time: </label>
                <input type="text" />
              </span>
            </div>
            <div className={classes.ingredients}>
              <label>Ingredients:</label>
              {ingredients.map((ingredient, i) => {
                return (
                  <span>
                    <label>{i + 1}</label>
                    <input
                      type="text"
                      value={ingredient.value || ""}
                      onChange={(e) => onIngredientChangeHandler(i, e)}
                    />
                  </span>
                );
              })}

              <input
                type="button"
                value="+"
                className={classes.add}
                onClick={addIngredientHandler}
              />
            </div>
            <div className={classes.directions}>
              <label>Directions:</label>
              {directions.map((direction, i) => {
                return (
                  <span>
                    <label>{i + 1}</label>
                    <input
                      type="text"
                      value={direction.value || ""}
                      onChange={(e) => onDirectionChangeHandler(i, e)}
                    />
                  </span>
                );
              })}

              <input
                type="button"
                value="+"
                className={classes.add}
                onClick={addDirectionHandler}
              />
            </div>
            <button className={classes.add}>SUBMIT</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddRecipe;
