import React, { useState, useEffect } from "react";
import classes from "./Header.module.scss";
import logo from "../../img/logo.png";
import icons_search from "../../img/icons.svg#icons-search";
const Header = () => {
  const [userInput, setUserInput] = useState("");
  const inputHandler = (e) => {
    console.log(e.target.value);
    setUserInput(e.target.value);
  };
  return (
    <header className={classes.header}>
      <img src={logo} alt="Logo" className={classes.header__logo} />
      <form className={classes.search}>
        <input
          type="text"
          className={classes.search__field}
          placeholder="Search over 1,000,000 recipes..."
          onChange={inputHandler}
        />
        <button className={classes.btn}>
          <svg className={classes.search__icon}>
            <use href={icons_search}></use>
          </svg>
          <span>Search</span>
        </button>
      </form>

      <nav className={classes.nav}>
        <ul className={classes.nav__list}>
          <li className={classes.nav__item}>
            <button className={classes.nav__btn}>
              {/* <svg className={classes.nav__icon}>
                      <use href="src/img/icons.svg#icon-edit"></use>
                    </svg> */}
              <span>Add recipe</span>
            </button>
          </li>
          <li className={classes.nav__item}>
            <button className={classes.nav__btn}>
              {/* <svg className={classes.nav__icon}>
                      <use href="src/img/icons.svg#icon-bookmark"></use>
                    </svg> */}
              <span>Bookmarks</span>
            </button>
            <div className={classes.bookmarks}>
              <ul className={classes.bookmarks__list}>
                <div className={classes.message}>
                  <div>
                    <svg>
                      <use href="src/img/icons.svg#icon-smile"></use>
                    </svg>
                  </div>
                  <p>No bookmarks yet. Find a nice recipe and bookmark it :)</p>
                </div>
              </ul>
            </div>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
