  const inputHandler = (e) => {
    console.log(e.target.value);
    setUserInput(e.target.value);
  };
  // console.log(data);
  return (
    <div className={classes.container}>
      <Header onInputHandler={inputHandler} onSubmitHandler={submitHandler} />
      {/* <div className={classes.search_results}> */}
      <Preview setCurrentID={setCurrentID} data={data} />
      <Recipe />
      {/* </div> */}
    </div>
  );
}

export default App;
