import React, { useState, useEffect } from "react";
import classes from "./App.module.scss";
import logo from "./img/logo.png";
import avatar from "./img/icons.svg#icons-user";
import arrowLeft from "./img/icons.svg#icon-arrow-left";
import arrowRight from "./img/icons.svg#icon-arrow-right";
import Preview from "./components/Preview/Preview";
function App() {
  const DUMMY_DATA = [
    {
      title: "Avocado Eggrolls",
      publisher: "Kavya Mandla",
      ingredients: [
        "2 avocados",
        "1/3 cup diced red onion",
        "1/3 cup sundried tomatoes",
      ],
      servings: 3,
      cookingTime: "45 minutes",
      directions: ["mix ingredients together", "wrap in eggroll"],
    },
    {
      title: "Avocado Eggrolls",
      publisher: "Kavya Mandla",
      ingredients: [
        "2 avocados",
        "1/3 cup diced red onion",
        "1/3 cup sundried tomatoes",
      ],
      servings: 3,
      cookingTime: "45 minutes",
      directions: ["mix ingredients together", "wrap in eggroll"],
    },
  ];
  const [data, setData] = useState([]);
  useEffect(() => {
    setData(DUMMY_DATA);
  }, []);

  console.log(data);
  return (
    <div className={classes.container}>
      <header className={classes.header}>
        <img src={logo} alt="Logo" className={classes.header__logo} />
        <form className={classes.search}>
          <input
            type="text"
            className={classes.search__field}
            placeholder="Search over 1,000,000 recipes..."
          />
          <button className={classes.btn}>
            {/* <svg className={classes.search__icon}>
                  <use href="src/img/icons.svg#icon-search"></use>
                </svg> */}
            <span>Search</span>
          </button>
        </form>

        <nav className={classes.nav}>
          <ul className={classes.nav__list}>
            <li className={classes.nav__item}>
              <button className={classes.nav__btn}>
                {/* <svg className={classes.nav__icon}>
                      <use href="src/img/icons.svg#icon-edit"></use>
                    </svg> */}
                <span>Add recipe</span>
              </button>
            </li>
            <li className={classes.nav__item}>
              <button className={classes.nav__btn}>
                {/* <svg className={classes.nav__icon}>
                      <use href="src/img/icons.svg#icon-bookmark"></use>
                    </svg> */}
                <span>Bookmarks</span>
              </button>
              <div className={classes.bookmarks}>
                <ul className={classes.bookmarks__list}>
                  <div className={classes.message}>
                    <div>
                      <svg>
                        <use href="src/img/icons.svg#icon-smile"></use>
                      </svg>
                    </div>
                    <p>
                      No bookmarks yet. Find a nice recipe and bookmark it :)
                    </p>
                  </div>
                </ul>
              </div>
            </li>
          </ul>
        </nav>
      </header>
      <div className={classes.search_results}>
        <Preview />
      </div>
    </div>
  );
}

export default App;
