import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
// import { useDispatch } from "react-redux";
import classes from "./App.module.scss";
import logo from "./img/logo.png";
import avatar from "./img/icons.svg#icons-user";
import arrowLeft from "./img/icons.svg#icon-arrow-left";
import arrowRight from "./img/icons.svg#icon-arrow-right";
import Preview from "./components/Preview/Preview";
import Header from "./components/Header/Header";
import Recipe from "./components/Recipe/Recipe";
// import { getRecipes } from "./actions/recipes";
function App() {
  const [currentID, setCurrentID] = useState(0);
  // const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(getRecipes());
  // }, [currentID, dispatch]);
  const DUMMY_DATA = [
    {
      title: "Avocado Eggrolls",
      publisher: "Kavya Mandla",
      ingredients: [
        "2 avocados",
        "1/3 cup diced red onion",
        "1/3 cup sundried tomatoes",
      ],
      servings: 3,
      cookingTime: "45 minutes",
      directions: ["mix ingredients together", "wrap in eggroll"],
      img:
        "https://www.thespruceeats.com/thmb/rkYibdpcITDVH22TxqpIIzne6T8=/940x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/AvocadoEggRolls-12-249294a23e2a43f9b5ffc25cff501a81.jpg",
    },
    {
      title: "Snickerdoodles",
      publisher: "Kavya Mandla",
      ingredients: [
        "2 avocados",
        "1/3 cup diced red onion",
        "1/3 cup sundried tomatoes",
      ],
      servings: 3,
      cookingTime: "45 minutes",
      directions: ["mix ingredients together", "wrap in eggroll"],
      img:
        "https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/delish-snickerdoodles-horizontal-1542063629.jpg",
    },
  ];
  const [data, setData] = useState([]);
  useEffect(() => {
    setData(DUMMY_DATA);
  }, []);
  const [userInput, setUserInput] = useState("");
  const [searched, setSearched] = useState("");
  const [selected, setSelected] = useState({});
  const [isSelected, setIsSelected] = useState(false);
  // const [filteredSearch, setFilteredSearch] = useState([]);
  // const inputHandler = (e) => {
  //   console.log(e.target.value);
  //   setUserInput(e.target.value);
  // };
  // useEffect(() => {});
  const submitHandler = (e) => {
    e.preventDefault();
    if (userInput != "") {
      setSearched(userInput);
    }
    setUserInput("");
  };

  const inputHandler = (e) => {
    // console.log(e.target.value);
    setUserInput(e.target.value.toLowerCase());
  };
  const filteredSearch = data.filter((recipe) => {
    return recipe.title.toLowerCase().includes(searched);
  });
  console.log("searched", searched);
  console.log(selected);
  return (
    <div className={classes.container}>
      <Header onInputHandler={inputHandler} onSubmitHandler={submitHandler} />
      {/* <div className={classes.search_results}> */}
      <Preview
        setCurrentID={setCurrentID}
        data={searched ? filteredSearch : data}
        searched={searched}
        setSelected={setSelected}
        setIsSelected={setIsSelected}
      />
      <Recipe data={selected} isSelected={isSelected} />
      {/* </div> */}
    </div>
  );
}

export default App;
