import React from "react";
import classes from "./AddRecipe.module.scss";
import icons from "../../img/icons.svg";
const AddRecipe = (props) => {
  const onHideHandler = () => {
    props.setAddingRecipe(false);
  };
  const categoryArr = [
    "vegetarian",
    "non-vegetarian",
    "breakfast",
    "lunch",
    "dinner",
    "desserts",
    "pescatarian",
  ];
  return (
    <div className={classes.overlay}>
      <div className={classes.container}>
        <div>
          <button className={classes.exit} onClick={onHideHandler}>
            <svg>
              <use xlinkHref={`${icons}#icon-exit`}></use>
            </svg>
          </button>
        </div>
        <h1>Add Recipe</h1>
        <div className={classes.details_one}>
          <form className={classes.form}>
            <div className={classes.name}>
              <label> Name: </label>
              <input type="text" />
            </div>
            <div className={classes.cuisine}>
              <label> Cuisine: </label>
              <input type="text" />
            </div>
            <div className={classes.category}>
              <label> Category: </label>
              <span>
                {categoryArr.map((category) => {
                  return (
                    <button className={classes.categoryButton}>
                      {category}
                    </button>
                  );
                })}
              </span>
            </div>
            <div className={classes.ingredients}>
              <label>Ingredients:</label>
              <input type="text" />
              {/* <button>+</button> */}
            </div>
            <div className={classes.directions}>
              <label>Directions:</label>
              <input type="text" />
              {/* <button>+</button> */}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddRecipe;
